import React, { useState } from "react";
import MarketSnapshot from "./MarketSnapshot";
import ExploreTab from "./ExploreTab";
import HoldingsTab from "./HoldingsTab";
import WatchlistTab from "./WatchlistTab";
import ScreenerTab from "./ScreenerTab";
import OrdersTab from "./OrdersTab";

const StocksTab = () => {
  const [activeStocksTab, setActiveStocksTab] = useState("explore");

  const stocksTabs = [
    { id: "explore", label: "Explore" },
    { id: "holdings", label: "Holdings" },
    { id: "watchlist", label: "Watchlist" },
    { id: "screener", label: "Screener" },
    { id: "orders", label: "Orders" },
  ];

  const renderTabContent = () => {
    switch (activeStocksTab) {
      case "explore":
        return <ExploreTab />;
      case "holdings":
        return <HoldingsTab />;
      case "watchlist":
        return <WatchlistTab />;
      case "screener":
        return <ScreenerTab />;
      case "orders":
        return <OrdersTab />;
      default:
        return <ExploreTab />;
    }
  };

  return (
    <div className="stocks-tab">
      <MarketSnapshot />

      <nav
        className="tab-navigation"
        role="tablist"
        aria-label="Stocks sections"
      >
        {stocksTabs.map((tab) => (
          <button
            key={tab.id}
            className={`tab-nav-item ${activeStocksTab === tab.id ? "active" : ""}`}
            onClick={() => setActiveStocksTab(tab.id)}
            role="tab"
            aria-selected={activeStocksTab === tab.id}
            aria-controls={`${tab.id}-content`}
          >
            {tab.label}
          </button>
        ))}
      </nav>

      <div
        className="tab-content"
        role="tabpanel"
        id={`${activeStocksTab}-content`}
        aria-labelledby={`${activeStocksTab}-tab`}
      >
        {renderTabContent()}
      </div>
    </div>
  );
};

export default StocksTab;
