import React from "react";
import { TrendingUp, TrendingDown } from "lucide-react";
import {
  formatCurrency,
  formatPercentage,
  getChangeClass,
} from "../../utils/formatters";

const MarketSnapshot = () => {
  const indices = [
    {
      name: "NIFTY 50",
      value: 19674.25,
      change: 1.25,
      changeValue: 243.5,
    },
    {
      name: "SENSEX",
      value: 65953.48,
      change: 1.18,
      changeValue: 769.25,
    },
    {
      name: "BANK NIFTY",
      value: 44362.8,
      change: -0.85,
      changeValue: -381.2,
    },
    {
      name: "NASDAQ",
      value: 13971.98,
      change: 0.67,
      changeValue: 92.45,
    },
    {
      name: "DOW JONES",
      value: 34098.16,
      change: -0.23,
      changeValue: -79.21,
    },
  ];

  return (
    <div className="market-snapshot">
      <div className="market-indices" role="region" aria-label="Market indices">
        {indices.map((index) => (
          <div key={index.name} className="market-index">
            <div className="index-name">{index.name}</div>
            <div className="index-value">
              {formatCurrency(index.value, false)}
            </div>
            <div className={`index-change ${getChangeClass(index.change)}`}>
              {index.change > 0 ? (
                <TrendingUp size={12} />
              ) : index.change < 0 ? (
                <TrendingDown size={12} />
              ) : null}
              <span>{formatPercentage(index.change)}</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MarketSnapshot;
