import React from "react";
import {
  User,
  CreditCard,
  Shield,
  Clock,
  FileText,
  Bell,
  HelpCircle,
  Settings,
  LogOut,
  CheckCircle,
  AlertCircle,
  ChevronRight,
} from "lucide-react";

const ProfileTab = () => {
  const profileSections = [
    {
      title: "Account Information",
      items: [
        {
          icon: User,
          label: "KYC Status",
          value: "Verified",
          status: "success",
          action: "View Details",
        },
        {
          icon: CreditCard,
          label: "Linked Bank Accounts",
          value: "2 accounts",
          action: "Manage",
        },
        {
          icon: Shield,
          label: "Security Settings",
          value: "Strong",
          status: "success",
          action: "Update",
        },
      ],
    },
    {
      title: "Trading & Investments",
      items: [
        { icon: Clock, label: "Transaction History", action: "View All" },
        { icon: FileText, label: "My Documents", action: "Manage" },
        {
          icon: Bell,
          label: "Notifications Center",
          value: "3 new",
          action: "View",
        },
      ],
    },
    {
      title: "Support & Settings",
      items: [
        { icon: HelpCircle, label: "Support & Help", action: "Get Help" },
        { icon: Settings, label: "App Preferences", action: "Customize" },
        { icon: LogOut, label: "Logout", action: "Sign Out", danger: true },
      ],
    },
  ];

  const kycStatus = {
    status: "Verified",
    completedSteps: 4,
    totalSteps: 4,
    steps: [
      { name: "Personal Details", completed: true },
      { name: "Address Verification", completed: true },
      { name: "Identity Verification", completed: true },
      { name: "Bank Account Details", completed: true },
    ],
  };

  const quickStats = [
    { label: "Portfolio Value", value: "₹4,85,672", change: "+14.28%" },
    { label: "Total Investments", value: "₹4,25,000", change: null },
    { label: "Active SIPs", value: "2", change: null },
    { label: "Insurance Policies", value: "2", change: null },
  ];

  return (
    <div className="profile-content">
      {/* User Header */}
      <div className="portfolio-summary" style={{ marginBottom: "24px" }}>
        <div
          style={{
            display: "flex",
            alignItems: "center",
            marginBottom: "16px",
          }}
        >
          <div
            style={{
              width: "60px",
              height: "60px",
              borderRadius: "50%",
              backgroundColor: "var(--primary-blue)",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              color: "white",
              fontSize: "24px",
              fontWeight: "600",
              marginRight: "16px",
            }}
          >
            JD
          </div>
          <div>
            <div
              style={{
                fontSize: "20px",
                fontWeight: "600",
                marginBottom: "4px",
              }}
            >
              John Doe
            </div>
            <div style={{ opacity: 0.8 }}>Customer ID: PW123456789</div>
          </div>
        </div>

        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(2, 1fr)",
            gap: "16px",
          }}
        >
          {quickStats.map((stat, index) => (
            <div key={index} style={{ textAlign: "center" }}>
              <div
                style={{ fontSize: "12px", opacity: 0.8, marginBottom: "4px" }}
              >
                {stat.label}
              </div>
              <div style={{ fontSize: "16px", fontWeight: "600" }}>
                {stat.value}
                {stat.change && (
                  <span
                    style={{
                      fontSize: "12px",
                      marginLeft: "4px",
                      color: "#10b981",
                    }}
                  >
                    {stat.change}
                  </span>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* KYC Status Card */}
      <div className="card" style={{ marginBottom: "24px" }}>
        <div
          style={{
            display: "flex",
            alignItems: "center",
            marginBottom: "12px",
          }}
        >
          <CheckCircle
            size={20}
            className="positive"
            style={{ marginRight: "8px" }}
          />
          <h3 style={{ fontSize: "16px", fontWeight: "600" }}>
            KYC Verification Complete
          </h3>
        </div>

        <div style={{ marginBottom: "12px" }}>
          <div
            style={{
              display: "flex",
              justifyContent: "space-between",
              marginBottom: "8px",
            }}
          >
            <span style={{ fontSize: "14px" }}>Verification Progress</span>
            <span style={{ fontSize: "14px", color: "var(--text-secondary)" }}>
              {kycStatus.completedSteps}/{kycStatus.totalSteps} Steps
            </span>
          </div>
          <div
            style={{
              width: "100%",
              height: "8px",
              backgroundColor: "var(--background-tertiary)",
              borderRadius: "4px",
              overflow: "hidden",
            }}
          >
            <div
              style={{
                width: `${(kycStatus.completedSteps / kycStatus.totalSteps) * 100}%`,
                height: "100%",
                backgroundColor: "var(--primary-green)",
                transition: "width 0.3s ease",
              }}
            />
          </div>
        </div>

        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(2, 1fr)",
            gap: "8px",
          }}
        >
          {kycStatus.steps.map((step, index) => (
            <div
              key={index}
              style={{
                display: "flex",
                alignItems: "center",
                fontSize: "12px",
              }}
            >
              {step.completed ? (
                <CheckCircle
                  size={14}
                  className="positive"
                  style={{ marginRight: "6px" }}
                />
              ) : (
                <AlertCircle
                  size={14}
                  className="neutral"
                  style={{ marginRight: "6px" }}
                />
              )}
              <span
                style={{
                  color: step.completed
                    ? "var(--primary-green)"
                    : "var(--text-secondary)",
                }}
              >
                {step.name}
              </span>
            </div>
          ))}
        </div>
      </div>

      {/* Profile Sections */}
      {profileSections.map((section, sectionIndex) => (
        <div key={sectionIndex} style={{ marginBottom: "24px" }}>
          <h2
            style={{
              fontSize: "18px",
              fontWeight: "600",
              marginBottom: "16px",
              paddingLeft: "16px",
            }}
          >
            {section.title}
          </h2>

          <div className="card" style={{ padding: "0" }}>
            {section.items.map((item, itemIndex) => {
              const IconComponent = item.icon;
              return (
                <div
                  key={itemIndex}
                  style={{
                    display: "flex",
                    alignItems: "center",
                    padding: "16px",
                    borderBottom:
                      itemIndex < section.items.length - 1
                        ? "1px solid var(--border-light)"
                        : "none",
                    cursor: "pointer",
                    transition: "background-color 0.2s ease",
                  }}
                  onMouseEnter={(e) => {
                    if (!item.danger) {
                      e.target.style.backgroundColor =
                        "var(--background-secondary)";
                    }
                  }}
                  onMouseLeave={(e) => {
                    e.target.style.backgroundColor = "transparent";
                  }}
                >
                  <div
                    style={{
                      width: "40px",
                      height: "40px",
                      borderRadius: "8px",
                      backgroundColor: item.danger
                        ? "#fee2e2"
                        : "var(--background-secondary)",
                      display: "flex",
                      alignItems: "center",
                      justifyContent: "center",
                      marginRight: "12px",
                    }}
                  >
                    <IconComponent
                      size={20}
                      color={
                        item.danger
                          ? "var(--error-red)"
                          : "var(--text-secondary)"
                      }
                    />
                  </div>

                  <div style={{ flex: 1 }}>
                    <div
                      style={{
                        fontSize: "16px",
                        fontWeight: "500",
                        color: item.danger
                          ? "var(--error-red)"
                          : "var(--text-primary)",
                        marginBottom: "2px",
                      }}
                    >
                      {item.label}
                    </div>
                    {item.value && (
                      <div
                        style={{
                          fontSize: "14px",
                          color:
                            item.status === "success"
                              ? "var(--primary-green)"
                              : "var(--text-secondary)",
                          display: "flex",
                          alignItems: "center",
                        }}
                      >
                        {item.status === "success" && (
                          <CheckCircle
                            size={14}
                            style={{ marginRight: "4px" }}
                          />
                        )}
                        {item.value}
                      </div>
                    )}
                  </div>

                  <div
                    style={{
                      display: "flex",
                      alignItems: "center",
                      color: "var(--text-secondary)",
                    }}
                  >
                    <span style={{ fontSize: "14px", marginRight: "4px" }}>
                      {item.action}
                    </span>
                    <ChevronRight size={16} />
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      ))}

      {/* App Information */}
      <div className="card" style={{ textAlign: "center", marginTop: "24px" }}>
        <div
          style={{
            fontSize: "24px",
            fontWeight: "700",
            color: "var(--primary-blue)",
            marginBottom: "8px",
          }}
        >
          Provitt
        </div>
        <div
          style={{
            fontSize: "14px",
            color: "var(--text-secondary)",
            marginBottom: "4px",
          }}
        >
          Wealth Management
        </div>
        <div style={{ fontSize: "12px", color: "var(--text-tertiary)" }}>
          Version 1.0.0
        </div>
      </div>
    </div>
  );
};

export default ProfileTab;
